package ck.ckrc.erie.warchess.example;

import ck.ckrc.erie.warchess.Main;
import ck.ckrc.erie.warchess.game.Chess;
import ck.ckrc.erie.warchess.game.DamageEvent;
import ck.ckrc.erie.warchess.game.DamageListener;
import ck.ckrc.erie.warchess.game.Player;
import ck.ckrc.erie.warchess.utils.DataPackage;
import ck.ckrc.erie.warchess.utils.ResourceSerialization;
import javafx.scene.Node;
import javafx.scene.canvas.GraphicsContext;
import javafx.scene.control.Label;
import javafx.scene.image.Image;
import javafx.scene.layout.GridPane;
import javafx.scene.paint.Color;

import java.util.Objects;

/**
 * 示范资源类棋子
 */
public class Miner extends Chess {

    public static final int productions=10, build_cost =10,max_hp=20;
    public static final double opticalA=0.1,opticalB=0.5,opticalMax=0.5,deltaO=0.0002,delta1=0.001;
    public static final Color origin=Color.AQUA;
    public static final double v1=origin.getRed(),v2=origin.getGreen(),v3=origin.getBlue();
    public static final String imageData="";
    public static final Image image=ResourceSerialization.getImageFromByteArray(ResourceSerialization.toByteArray(imageData));
    public static final String energyKey="example.red-stoneFlux";
    private final DamageListener myDmgListener;
    public static final String className="ck.ckrc.erie.warchess.example.Miner";
    private double current=opticalA,animDelta=0;
    private boolean direction=true;

    public Miner(int x,int y,Player p){
        this.x=x;
        this.y=y;
        this.teamFlag=p.getTeamFlag();
        this.hp=max_hp;
        myDmgListener= damage -> {
            if(hp>damage){
                hp-=(int)damage;
                return 0;
            }else{
                double tmp=damage-hp;
                hp=0;
                return tmp;
            }
        };

        Main.currentGameEngine.registerDamageListener(this,1,myDmgListener,x,y);
        p.setStatus(energyKey,(int)p.getStatus(energyKey)-build_cost);

    }

    @Override
    public Node showPanel() {
        GridPane pane=new GridPane();
        Label title=new Label("能量塔");
        Label position=new Label("position:"+'('+x+','+y+')');
        Label team=new Label("team:"+teamFlag);
        pane.addRow(0,title);
        Label status1=new Label("HP:"+hp+'('+max_hp+')');
        Label status2=new Label("能量:"+Main.currentGameEngine.getPlayer(teamFlag).getStatus(Miner.energyKey));
        pane.addRow(1,status1,status2);
        pane.addRow(2, position,team);
        return pane;
    }

    public static Node showData(){
        GridPane pane=new GridPane();
        Label title=new Label("能量塔");
        title.setPrefWidth(100);
        pane.addRow(0, title);
        Label status=new Label("maxHP:"+max_hp);
        pane.addRow(1, status);
        Label cost=new Label("build cost:"+build_cost);
        pane.addRow(2, cost);
        Label product=new Label("production:"+productions);
        pane.setPrefSize(200, 25);
        return pane;
    }

    @Override
    public boolean checkEvent(DamageEvent evt) {
        return false;
    }

    @Override
    public boolean checkListener(DamageListener listener) {
        if(hp<=0)return false;
        return myDmgListener==listener;
    }

    @Override
    public void roundEnd() {
        animDelta=opticalMax;
    }

    @Override
    public void roundBegin() {
        if(!Objects.equals(teamFlag, Main.currentGameEngine.getCurrentTeam()))return;
        Main.currentGameEngine.getPlayer(teamFlag).setStatus(energyKey,(int)Main.currentGameEngine.getPlayer(teamFlag).getStatus(energyKey)+productions);
    }

    public static boolean checkPlaceRequirements(Player player,int x,int y) {
        return (int)player.getStatus(energyKey)>= build_cost;
    }


    public static void playerInit(Player player){
        player.setStatus(energyKey,50);
    }

    @Override
    public DataPackage getDataPackage() {
        return DataPackage.generateDataPackage(this,this.getClass());
    }

    @Override
    public void syncDataPackage(DataPackage pack) {
        super.syncDataPackage(pack);
        DataPackage.processDataPackage(this,this.getClass(),pack);
    }

    @Override
    public Image paint(long delta) {
        //TODO add Image
        return image;
    }

    @Override
    public void drawSpecialEffect(GraphicsContext context, long delta) {
        current=direction?current-deltaO*delta:current+deltaO*delta;
        if(current<opticalA)direction=false;
        if(current>opticalB)direction=true;
        if(animDelta>0) {
            context.setFill(Color.color(v1,v2,v3,Math.min(1,animDelta+current)));
            animDelta-=delta1*delta;
        }else
            context.setFill(Color.color(v1,v2,v3,current));
        context.fillRect(x*60,y*60,60,60);
    }
}
